import React, { useEffect, useState } from 'react';
import Like from './Like';
import Comment from './Comment';

export default function Posts() {
    const [posts , setPosts] = useState([]);
    useEffect(() => {
        fetch('http://127.0.0.1:8080/posts')
        .then(res => res.json())
        .then(res => setPosts(res))
    });
  return (
    <div>
      {posts.map(d =>
        <div className='card mb-4 pt-4 black shadow'>
             <div className='row-cols-2 row text-center'>
               <div><h6 className='card back mb-4 shadow'>{d.userId}</h6></div>
               <div><h4 className='text-white'>{d.title}</h4></div>
            </div>
            <div>
               <p className='card text-white text-bold'>{d.content}</p>
            </div>
            <div className='row row-cols-2'>
              <div className="like"><Like /></div>
              <div><Comment /></div>
            </div>
        </div>
      )}
    </div>
  );
}
